'-- Developed by Tobias Arnhold - http://apex-at-work.blogspot.com/',
'-- tobias-arnhold@hotmail.de',
'FUNCTION render_loadingIcon (p_dynamic_action IN apex_plugin.t_dynamic_action, p_plugin IN apex_plugin.t_plugin)',
'  RETURN apex_plugin.t_dynamic_action_render_result',
'AS',
'  -- Return variable',
'  v_result                      apex_plugin.t_dynamic_action_render_result;',
'',
'  -- Save plugin-parameter in local variables',
'  iconAction        apex_application_page_regions.attribute_01%type := p_dynamic_action.attribute_01;',
'  iconText          apex_application_page_regions.attribute_02%type := p_dynamic_action.attribute_02;',
'  iconImage         apex_application_page_regions.attribute_03%type := p_dynamic_action.attribute_03;',
'  iconWidth         apex_application_page_regions.attribute_04%type := p_dynamic_action.attribute_04;',
'  iconHeight        apex_application_page_regions.attribute_05%type := p_dynamic_action.attribute_05;',
'  iconOpacity       apex_application_page_regions.attribute_06%type := p_dynamic_action.attribute_06;',
'  iconColor         apex_application_page_regions.attribute_07%type := UPPER(p_dynamic_action.attribute_07);',
'  backgroundOpacity apex_application_page_regions.attribute_08%type := p_dynamic_action.attribute_08;',
'  iconDefaultImage  apex_application_page_regions.attribute_09%type := p_dynamic_action.attribute_09;',
'  ',
'  -- Variables for javascript parts',
'  completeCode varchar2(2000);',
'',
'begin',
'/* During plug-in development it''s very helpful to have some debug information */',
'  IF APEX_APPLICATION.g_debug THEN',
'    apex_plugin_util.debug_dynamic_action (p_plugin => p_plugin, p_dynamic_action => p_dynamic_action);',
'  END IF;',
'',
'/* Load JavaScript/CSS Libraries */',
'  apex_javascript.add_library (p_name => ''loading.icon'', p_directory => p_plugin.file_prefix, p_version => null);',
'  ',
'/* Escaping der Parameter */',
'  iconText          := apex_plugin_util.escape(iconText, true);',
'  iconImage         := apex_plugin_util.escape(iconImage, true);',
'  iconWidth         := apex_plugin_util.escape(iconWidth, true);',
'  iconHeight        := apex_plugin_util.escape(iconHeight, true);',
'  iconOpacity       := apex_plugin_util.escape(iconOpacity, true);',
'  iconColor         := apex_plugin_util.escape(iconColor, true);',
'  backgroundOpacity := apex_plugin_util.escape(backgroundOpacity, true);',
'',
'/* Function declaration */',
'  completeCode := ''function(){''||chr(10);',
'  ',
'/* Set default image */',
'if iconImage = ''default'' then',
' if iconDefaultImage = ''ICON1'' then',
'   iconImage := p_plugin.file_prefix || ''default.loading.icon1.gif'';',
' elsif iconDefaultImage = ''ICON2'' then',
'   iconImage := p_plugin.file_prefix || ''default.loading.icon2.gif'';',
' elsif iconDefaultImage = ''ICON3'' then',
'   iconImage := p_plugin.file_prefix || ''default.loading.icon3.gif'';',
' elsif iconDefaultImage = ''ICON4'' then',
'   iconImage := p_plugin.file_prefix || ''default.loading.icon4.gif'';',
' elsif iconDefaultImage = ''ICON5'' then',
'   iconImage := p_plugin.file_prefix || ''default.loading.icon5.gif'';',
' end if;',
'end if;',
'  ',
'/* Code definition*/',
'if iconAction = ''START'' then',
'  /* Start LoadIcon */',
'  completeCode := completeCode || ''fnc_startLoadIcon("''||iconText||''","''||iconImage||''",''||iconWidth||'',''||iconHeight||'',''||iconOpacity||'',"''||iconColor||''",''||backgroundOpacity||'');''||chr(10);',
'elsif iconAction = ''STOP'' then',
'  /* Stop LoadIcon */',
'  completeCode := completeCode || ''fnc_stopLoadIcon();''||chr(10);',
'elsif iconAction = ''PRELOAD_IMAGE'' then',
'  /* Preload Image */',
'  completeCode := completeCode || ''var v_load_image = new Image(); v_load_image.src = "''||iconImage||''";''||chr(10);',
'end if;',
'  ',
'/* Finish Code */',
'  completeCode := completeCode || ''}'';',
'',
'  v_result.javascript_function := completeCode;',
'  return v_result;',
'end render_loadingIcon;'))
,p_api_version=>1
,p_render_function=>'render_loadingIcon'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_help_text=>wwv_flow_string.join(wwv_flow_t_varchar2(
'<p>',
'	This plug-in creates a loading icon frame on top of all other elements. You can use different settings to build up your own loading theme.</p>',
'<p>',
'	<strong>How To:</strong></p>',
'<p>',
'	Create a dynamic action &quot;Before Page Submit&quot;, then execute the &quot;Loading Icon&quot; plug-in with the action &quot;Start LoadingIcon&quot;.</p>',
'<p>',
'	If you have problems displaying the icon. Build another dynamic action &quot;Page Load&quot; with the &quot;Loading Icon&quot; plug-in and the action &quot;Preload Image&quot;.</p>',
''))
